import './App.css';
import data from '../src/data.json';
import DisplayAnt from './Components/DisplayAnt';
import { useEffect, useState } from 'react';
import DisplayButton from './Components/DisplayButton';

function App() {
  const [load, setLoad] = useState(false);
  const [start, setStart] = useState(false);
  const [ants, setAnts] = useState([]);  
  const [likelihoods, setLikelihood] = useState([]);

  const generateAntWinLikelihoodCalculator = () => {
    const delay = 7000 + Math.random() * 7000;
    const likelihoodOfAntWinning = Math.random();

    return (callback) => {
        setTimeout(() => {
            callback(likelihoodOfAntWinning);
        }, delay);
    };
  }

  const calculateLikelihood = (array) => {
    const callback = generateAntWinLikelihoodCalculator();

    callback((likelihood) => {      
      const tmp = likelihoods;
      tmp.push(Math.round(likelihood * 100) / 100);      
      setLikelihood(tmp);
      // assign 
      tmp.forEach((lk, index) => {
        console.log(lk, index);
        array[index] = {...array[index], 'likelihood': lk};
      })
      // sort by likelihood
      array.sort((a, b) => (a.likelihood > b.likelihood) ? -1 : (b.likelihood > a.likelihood ? 1 : 0));
      console.log(array);  
      setAnts(array, tmp);
    })                    
  }

  useEffect(() => {
    calculateLikelihood(ants);
  }, [start])

  return (
    <div className="App">
      <header className="App-header">        
      </header>
      <DisplayButton setStart={setStart} start={start} setAnts={setAnts} data={data} calculateLikelihood={calculateLikelihood}/>
      {ants.length > 0 && <DisplayAnt ants={ants}/>}
    </div>
  );
}

export default App;
